 let q = m.quoted || m
 let mime = (q.msg || q).mimetype || ''
 if (!/image/.test(mime)) return m.reply('❌ Balas gambar dengan perintah *pixpic* untuk meningkatkan kualitasnya.')
 try {
 await conn.sendMessage(m.chat, { react: { text: '🕒', key: m.key } })
 let imgBuffer = await q.download()
 const FormData = require('form-data')
 const form = new FormData()
 form.append('reqtype', 'fileupload')
 form.append('fileToUpload', imgBuffer, 'image.png')
 const upload = await axios.post('https://catbox.moe/user/api.php', form, {
 headers: form.getHeaders()
 })
 if (!upload.data || !upload.data.includes('https://')) return m.reply('❌ Gagal upload ke Catbox.')
 let imageUrl = upload.data.trim()
 await conn.sendMessage(m.chat, { react: { text: '⚙️', key: m.key } })
 const res = await fetch(`https://api.nekorinn.my.id/tools/pxpic-upscale?imageUrl=${encodeURIComponent(imageUrl)}`)
 const json = await res.json()
 if (!json.result) return m.reply('❌ Gagal memproses gambar.')
 await conn.sendMessage(m.chat, {
 image: { url: json.result },
 caption: '💯 *Image quality improved (HD)*',
 }, { quoted: m })
 } catch (err) {
 console.error(err)
 m.reply('❌ Terjadi kesalahan saat memproses gambar.')
